package loops;

public class ForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		// for (Initialize;condition;increment)
		for (i = 0; i <= 10; i++) {
			System.out.println(" This is value of  i incremental : " + i);

		}

		System.out.println("***************************");
		
		// for (Initialize;condition;decrement)
		for (int j = 10; j >= 0; j--) {
			System.out.println(" This is value of  i decremental : " + j);
		}

	}

}
