package loops;

public class NestedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int r = 0; r <= 10; r++) {
			for (int c = 0; c <= 10; c++) {
				  System.out.println("Value of c :"+c);
				 //System.out.print(c+ " " );
				   System.out.print(c); // print all values in a line
				//System.out.println("********");
							}
			System.out.println();
		}

	}

}
